Date.time=function(){return(new Date()).getTime()};String.prototype.quotemeta=function(){return this.replace(/\W/g,"\\$&")};String.prototype.zenkaku=function(){return this.replace(/(\w)/g,function(a){return String.fromCharCode(a.charCodeAt(0)+65248)})};String.prototype.hankaku=function(){return this.replace(/([Ａ-Ｚａ-ｚ０-９＿＠＋－，．：；])/g,function(a){return String.fromCharCode(a.charCodeAt(0)-65248)})};Array.prototype.asort=function(a){this.sort(function(d,c){return(d[a]>c[a])?1:-1})};Array.prototype.arsort=function(a){this.sort(function(d,c){return(d[a]<c[a])?1:-1})};